Liquid.Block.prototype.renderAll = function(list, context) {
  return (list || []).map(function(token, i){
    var output = '';
    try { // hmmm... feels a little heavy
      output = ( token['render'] ) ? token.render(context) : token;
    } catch(e) {
      
      console.log(context.handleError(e));
    }
    return output;
  });
};
